/* ==UserStyle==
@name           eloquentjavascript.net
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("eloquentjavascript.net") {
    body {
        font-family: Roboto, sans-serif;
        font-weight: 400
    }

    pre {
        border: 1px solid #ccc;
        border-radius: 8px;
        background-color: #eee;
    }


}

/* ==UserStyle==
@name         OpenAI ChatGPT Nord Cyber Theme DARK MODE
@namespace    USO Archive
@author       Modified for Nord & Cyber Style
@description  A Nord-inspired cyberpunk theme with Tron-style animations for ChatGPT
@version      20251016.01.00
@license      NONE
@preprocessor uso
==/UserStyle== */
/* Nord Color Palette:
   Polar Night: #2E3440, #3B4252, #434C5E, #4C566A
   Snow Storm: #D8DEE9, #E5E9F0, #ECEFF4
   Frost: #8FBCBB, #88C0D0, #81A1C1, #5E81AC
   Aurora: #BF616A (red), #D08770 (orange), #EBCB8B (yellow), #A3BE8C (green), #B48EAD (purple)
*/
@-moz-document domain("chatgpt.com") {
	@import url('https://fonts.googleapis.com/css2?family=Lexend:wght@300;400;500;600;700&display=swap');

	/* Override body font */
	body {
		font-family: 'Lexend', sans-serif !important;
	}

	/* Giá»¯ monospace cho code */
	code,
	pre,
	kbd,
	samp {
		font-family: 'Fira Code', 'JetBrains Mono', monospace !important;
	}
	/* Cyber Glow Animation */
	@keyframes cyber-glow {
		0%,
		100% {
			text-shadow: 0 0 10px #88C0D0, 0 0 20px #88C0D0, 0 0 30px #5E81AC;
		}
		50% {
			text-shadow: 0 0 20px #88C0D0, 0 0 30px #5E81AC, 0 0 40px #81A1C1;
		}
	}

	/* Tron-style Bounce */
	@keyframes bounce_chatgpt {
		0% {
			animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
			opacity: 0;
			transform: translateY(-45px) scale(0.95);
			filter: brightness(1.5);
		}
		24% {
			opacity: 1;
			filter: brightness(1.2);
		}
		40% {
			animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
			transform: translateY(-24px) scale(0.98);
		}
		65% {
			transform: translateY(-12px) scale(0.99);
		}
		82% {
			transform: translateY(-6px) scale(1);
		}
		93% {
			transform: translateY(-4px);
		}
		25%,
		55%,
		75%,
		87% {
			animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
			transform: translateY(0) scale(1);
		}
		100% {
			opacity: 1;
			transform: translateY(0) scale(1);
			filter: brightness(1);
		}
	}

	/* Neon Border Pulse */
	@keyframes neon-pulse {
		0%,
		100% {
			box-shadow: 0 0 5px #88C0D0, 0 0 10px #88C0D0, inset 0 0 5px #88C0D0;
		}
		50% {
			box-shadow: 0 0 10px #88C0D0, 0 0 20px #5E81AC, inset 0 0 10px #5E81AC;
		}
	}

	/* Cyber Loading Animation */
	@keyframes cyber-loading {
		0% {
			transform: translateX(-100%);
		}
		100% {
			transform: translateX(100%);
		}
	}

	/* Cyber Border Animation */
	@keyframes border-flow {
		0% {
			border-color: #88C0D0;
		}
		50% {
			border-color: #5E81AC;
		}
		100% {
			border-color: #88C0D0;
		}
	}

	/* Selection Colors */
	::-moz-selection {
		background: #5E81AC;
		color: #ECEFF4;
	}
	::selection {
		background: #5E81AC;
		color: #ECEFF4;
	}

	/* Base Styles */
	body {
		color: #ECEFF4;
		position: relative;
	}

	/* Cyber Grid Background */
	body::before {
		content: '';
		position: fixed;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		background: linear-gradient(90deg, rgba(136, 192, 208, 0.03) 1px, transparent 1px),
		linear-gradient(rgba(136, 192, 208, 0.03) 1px, transparent 1px);
		background-size: 50px 50px;
		pointer-events: none;
		z-index: 0;
	}

	#headlessui-portal-root .justify-center > div.relative,
	.dark body,
	.dark html,
	body {
		background-color: #2E3440;
		font-family: 'JetBrains Mono', 'Fira Code', Consolas, monospace;
		font-weight: normal;
	}

	.bg-token-sidebar-surface-primary {
		background-color: #3B4252;
		border-right: 1px solid #88C0D0;
		box-shadow: 2px 0 10px rgba(136, 192, 208, 0.1);
	}

	#headlessui-portal-root .btn-danger,
	.markdown th,
	div > .hover\:bg-gray-500\/10 {
		background-color: #BF616A;
		border: 1px solid #D08770;
	}

	@media (min-width:768px) {
		.md\:pb-\[8vh\] {
			position: relative;
			z-index: -5;
		}
	}

	/* Cyber H1 */
	h1 {
		font-size: 2rem;
		color: #88C0D0 !important;
		text-shadow: 0 0 10px #88C0D0, 0 0 20px #5E81AC;
		animation: 2s ease-out forwards bounce_chatgpt, 3s ease-in-out infinite cyber-glow;
		text-transform: uppercase;
		letter-spacing: 0.1em;
		font-weight: 700;
	}

	h1 span {
		display: none;
	}

	.dark .dark\:text-gray-100,
	a {
		color: #88C0D0;
		transition: all 0.3s ease;
	}

	a:hover {
		color: #8FBCBB;
		text-shadow: 0 0 5px #88C0D0;
	}

	/* Cyber Borders */
	main button,
	main li.group {
		border-color: #5E81AC !important;
		transition: all 0.3s ease;
	}

	main button:focus {
		border-color: #88C0D0 !important;
		animation: neon-pulse 2s infinite;
	}

	.input-container,
	.output-container {
		border: 1px solid #5E81AC;
		background-color: #3B4252;
		box-shadow: 0 0 10px rgba(94, 129, 172, 0.2);
	}

	#headlessui-portal-root .grow.justify-center .text-gray-500,
	.user-input {
		color: #8FBCBB;
		text-shadow: 0 0 5px rgba(143, 188, 187, 0.3);
	}

	#headlessui-portal-root .grow.justify-center svg.text-green-700,
	.assistant-output,
	textarea + button.text-gray-500 {
		color: #A3BE8C;
		text-shadow: 0 0 5px rgba(163, 190, 140, 0.3);
	}

	.timestamp {
		color: #4C566A;
		font-family: 'Courier New', monospace;
	}

	/* Cyber Buttons */
	.send-button {
		background: linear-gradient(135deg, #5E81AC 0%, #81A1C1 100%);
		color: #ECEFF4;
		border: 1px solid #88C0D0;
		box-shadow: 0 0 10px rgba(136, 192, 208, 0.3);
		transition: all 0.3s ease;
	}

	.send-button:hover {
		background: linear-gradient(135deg, #81A1C1 0%, #88C0D0 100%);
		box-shadow: 0 0 20px rgba(136, 192, 208, 0.5);
		transform: translateY(-2px);
	}

	svg[viewBox = "0 0 41 41"] {
		filter: drop-shadow(0 0 5px #88C0D0);
	}

	.relative.rounded-sm {
		border-radius: 100%;
		background: linear-gradient(135deg, #88C0D0 0%, #5E81AC 100%) !important;
		box-shadow: 0 0 15px rgba(136, 192, 208, 0.5);
	}

	/* Sidebar Styles */
	.bg-gray-900.md\:fixed,
	.dark .dark\:bg-gray-900,
	.scrollbar-trigger,
	.scrollbar-trigger a.bg-gray-900 {
		background-color: #3B4252;
	}

	.scrollbar-trigger > nav.p-3 {
		padding: 0 .5rem 0 .04rem;
	}

	.scrollbar-trigger .bg-gray-800,
	.scrollbar-trigger a.bg-gray-800:hover {
		background-color: #434C5E;
		border-left: 2px solid #88C0D0;
		transition: all 0.3s ease;
	}

	.scrollbar-trigger .bg-gray-800:hover {
		background-color: #4C566A;
		box-shadow: inset 2px 0 10px rgba(136, 192, 208, 0.2);
	}

	.hover\:bg-token-surface-primary:hover .bg-gradient-to-l,
	.scrollbar-trigger .bg-gray-800 .bg-gradient-to-l,
	.scrollbar-trigger .hover\:bg-\[\#2A2B32\] .bg-gradient-to-l {
		background-image: inherit;
	}

	#headlessui-portal-root button[aria-selected = true],
	.group[data-headlessui-state ~= open] .group-ui-open\:bg-gray-800,
	.scrollbar-trigger .hover\:bg-\[\#2A2B32\]:hover,
	.scrollbar-trigger .hover\:bg-gray-800:hover {
		background-color: #434C5E;
		border-left: 3px solid #88C0D0;
		box-shadow: 0 0 15px rgba(136, 192, 208, 0.3);
	}

	.scrollbar-trigger .bg-\[\#050509\],
	.scrollbar-trigger .ui-open\:bg-gray-800[data-headlessui-state ~= open] {
		background-color: #2E3440;
	}

	.scrollbar-trigger .bg-\[\#050509\].opacity-100 {
		opacity: .96;
	}

	.scrollbar-trigger .bg-\[\#050509\] .bg-gray-700:hover {
		background-color: #5E81AC !important;
		box-shadow: 0 0 10px rgba(94, 129, 172, 0.3);
	}

	.scrollbar-trigger .bg-\[\#050509\] .bg-gray-700:hover:first-child {
		background-color: #EBCB8B !important;
	}

	.scrollbar-trigger .bg-\[\#050509\] .bg-gray-700:hover:last-child {
		background-color: #BF616A !important;
	}

	.scrollbar-trigger .border-white\/20 {
		border-color: #5E81AC;
	}

	/* Main Content Area */
	.dark .dark\:bg-gray-700 {
		background-color: #3B4252;
		box-shadow: inset 0 1px 0 0 rgba(136, 192, 208, 0.1),
		0 0 20px 0 rgba(0, 0, 0, 0.4);
	}

	.dark .dark\:bg-gray-800,
	.dark .dark\:hover\:bg-gray-700:hover,
	.hover\:bg-token-surface-primary:hover,
	span > .hover\:bg-gray-500\/10:hover {
		background-color: #434C5E;
		transition: all 0.3s ease;
	}

	#headlessui-portal-root .btn-danger:hover,
	div > .hover\:bg-gray-500\/10:hover {
		background-color: #D08770;
		box-shadow: 0 0 10px rgba(208, 135, 112, 0.4);
	}

	.dark .dark\:bg-gray-700 button:hover {
		cursor: pointer;
		transform: translateX(5px);
		transition: transform 0.3s ease;
	}

	.disabled\:text-gray-400:disabled:hover,
	.gizmo .gizmo\:text-gizmo-gray-600 {
		color: #D8DEE9;
	}

	.bg-token-surface-primary,
	.dark .dark\:hover\:bg-\[\#2A2B32\]:hover {
		background-color: #4C566A;
	}

	.dark .dark\:bg-\[\#444654\],
	.dark .dark\:hover\:bg-gray-800:hover,
	.dark .dark\:hover\:bg-token-surface-primary:hover {
		background-color: #434C5E !important;
	}

	.h-full.w-full.bg-white .rounded-sm {
		background-color: inherit !important;
	}

	.dark .dark\:bg-\[\#272832\],
	.group.hover\:bg-token-surface-primary {
		background-color: #BF616A;
		border: 1px solid #D08770;
	}

	ol li .group.hover\:bg-token-surface-primary {
		background-color: #3B4252;
	}

	.dark .dark\:bg-\[\#444654\] {
		border-top: 1px solid #5E81AC;
		border-bottom: 1px solid #88C0D0;
		box-shadow: inset 0 -5px 10px 0 rgba(136, 192, 208, 0.1);
		background-color: #3B4252 !important;
	}

	/* Cyber Button Styles */
	.dark button.btn-neutral,
	.scrollbar-trigger .btn.btn-dark {
		background: linear-gradient(135deg, #434C5E 0%, #4C566A 100%);
		border: 1px solid #5E81AC;
		box-shadow: 0 0 10px rgba(94, 129, 172, 0.2);
		transition: all 0.3s ease;
	}

	.dark button.btn-neutral:hover,
	.scrollbar-trigger .btn.btn-dark:hover {
		background: linear-gradient(135deg, #4C566A 0%, #5E81AC 100%);
		box-shadow: 0 0 15px rgba(136, 192, 208, 0.4);
		transform: translateY(-2px);
	}

	.scrollbar-trigger .sticky .text-gray-500 {
		background-color: #2E3440;
		border-bottom: 1px solid #5E81AC;
		line-height: .75rem;
		color: #88C0D0;
		letter-spacing: .02em;
		text-transform: uppercase;
	}

	.absolute.bottom-0 .text-center {
		padding: 0 0 20px;
		text-align: left;
	}

	@media only screen and (min-width:1065px) {
		.absolute.bottom-0 .px-3.text-center {
			padding: initial;
			background-color: inherit;
			text-align: left;
			height: 0;
		}
		.absolute.bottom-0 .text-center span {
			font-size: 0;
			position: relative;
			display: none;
		}
		.absolute.bottom-0 .text-center span a {
			font-size: 0;
			position: absolute;
			text-decoration: none;
			top: -50px;
			left: 20px;
		}
		.absolute.bottom-0 .text-center span a::before {
			content: "?";
			display: flex;
			justify-content: center;
			align-items: center;
			text-align: center;
			font-size: 14px;
			position: relative;
			cursor: pointer;
			width: 24px;
			height: 24px;
			background-color: rgba(94, 129, 172, 0.3);
			border: 1px solid #88C0D0;
			border-radius: 100%;
			box-shadow: 0 0 10px rgba(136, 192, 208, 0.3);
		}
		.absolute.bottom-0 .text-center span a:hover::after {
			content: "ChatGPT â Release Notes";
			display: block;
			position: absolute;
			top: -30px;
			left: -5px;
			padding: 5px;
			background-color: #3B4252;
			border: 1px solid #88C0D0;
			opacity: .9;
			font-size: 12px;
			border-radius: 4px;
			white-space: nowrap;
			z-index: 1000;
			box-shadow: 0 0 15px rgba(136, 192, 208, 0.4);
		}
		.absolute.bottom-0 .text-center span a:focus::before {
			border: 2px solid #88C0D0;
			animation: neon-pulse 2s infinite;
		}
	}

	.absolute.bottom-0 .text-center.stretch {
		background-color: #4C566A;
		border-radius: 4px;
		border: 1px solid #5E81AC;
	}

	.absolute.bottom-0 form.stretch > div > div > div.justify-center {
		justify-content: right;
	}

	.absolute.bottom-0 form.stretch > div > div > div > button {
		padding: 2px 6px;
		font-size: .7em;
	}

	/* Textarea Styles */
	textarea {
		max-height: 35vh !important;
		background-color: #3B4252 !important;
		border: 1px solid #5E81AC !important;
		color: #ECEFF4 !important;
		box-shadow: inset 0 0 10px rgba(94, 129, 172, 0.1);
	}

	textarea:focus {
		border-color: #88C0D0 !important;
		box-shadow: inset 0 0 10px rgba(136, 192, 208, 0.2),
		0 0 15px rgba(136, 192, 208, 0.3) !important;
		animation: neon-pulse 2s infinite;
	}

	textarea + button.text-gray-500:hover {
		background-color: inherit !important;
		color: #88C0D0 !important;
		text-shadow: 0 0 5px #88C0D0;
	}

	/* Markdown Styles */
	.markdown .bg-black {
		background-color: #2E3440;
	}

	.markdown .bg-gray-800 {
		background-color: #434C5E;
		border: 1px solid #5E81AC;
	}

	.markdown .bg-gray-800 span,
	.markdown th {
		font-size: 16px;
	}

	.markdown h1,
	.markdown th {
		text-shadow: 0 0 10px rgba(136, 192, 208, 0.5);
	}

	.markdown hr {
		border-top: 1px solid #4C566A;
		border-bottom: 1px solid #5E81AC;
		box-shadow: 0 0 5px rgba(94, 129, 172, 0.3);
	}

	.markdown th {
		font-weight: 600;
		background-color: #5E81AC;
		color: #ECEFF4;
		text-transform: uppercase;
		letter-spacing: 0.05em;
	}

	.markdown *,
	.markdown ::after,
	.markdown ::before {
		border-color: #4C566A;
	}

	.markdown th,
	.markdown th::after,
	.markdown th::before {
		border-color: #88C0D0;
	}

	.markdown thead tr th,
	.markdown thead tr th::after,
	.markdown thead tr th::before {
		border-bottom: 2px solid #88C0D0;
	}

	.markdown a:hover {
		text-decoration: underline;
		color: #8FBCBB;
		text-shadow: 0 0 5px #88C0D0;
	}

	.markdown h1 {
		color: #88C0D0 !important;
		font-size: 1.8em !important;
	}

	.markdown strong {
		color: #ECEFF4;
		font-weight: 700;
	}

	.markdown li code,
	.markdown p code,
	.markdown h3 code,
	.markdown h4 code {
		color: #EBCB8B;
		background-color: #3B4252 !important;
		border: 1px solid #5E81AC;
		padding: 2px 6px;
		border-radius: 3px;
		font-family: 'Fira Code', monospace;
	}

	.markdown li pre code {
		background-color: #2E3440 !important;
		border: 1px solid #5E81AC;
		box-shadow: 0 0 10px rgba(94, 129, 172, 0.2);
	}

	/* Icon Hover Effects */
	.group.w-full button:hover {
		background-color: rgba(136, 192, 208, 0.1) !important;
	}

	.group.w-full .flex.justify-between .flex.gap-1 button:first-child svg.h-4.w-4:hover,
	.group.w-full .flex.justify-between.items-center button[title = "This response was better than the previous response"] svg,
	.group.w-full .flex.justify-between.items-center button[title = "This response was better than the previous response"] svg:hover {
		color: #A3BE8C !important;
		filter: drop-shadow(0 0 5px #A3BE8C);
	}

	.group.w-full .flex.justify-between .flex.gap-1 button:last-child svg.h-4.w-4:hover,
	.group.w-full .flex.justify-between button + button svg.h-4.w-4:hover,
	.group.w-full .flex.justify-between.items-center button[title = "This response was worse than the previous response"] svg {
		color: #BF616A !important;
		filter: drop-shadow(0 0 5px #BF616A);
	}

	.group.w-full .flex.justify-between.items-center button[title = "This response was the same in quality"] svg,
	.group.w-full .flex.justify-between.items-center button[title = "This response was the same in quality"] svg:hover {
		color: #EBCB8B !important;
		border-color: #EBCB8B !important;
		filter: drop-shadow(0 0 5px #EBCB8B);
	}

	.group.w-full .flex.justify-between button svg.h-4.w-4:hover,
	.group.w-full .flex.justify-between.items-center button[title = "This response was the same in quality"] path[d = "M5 9h14m-14 6h14"],
	.group.w-full button svg.h-4.w-4:hover {
		color: #88C0D0 !important;
		filter: drop-shadow(0 0 5px #88C0D0);
	}

	/* Modal Styles */
	#headlessui-portal-root .dark\:bg-gray-800\/90 {
		background-color: rgba(46, 52, 64, 0.95) !important;
		backdrop-filter: blur(10px);
	}

	#headlessui-portal-root .justify-center > div.relative.bg-transparent {
		background-color: initial;
	}

	#headlessui-portal-root .justify-center > div.relative {
		border: 1px solid #5E81AC;
		box-shadow: 0 0 30px rgba(136, 192, 208, 0.3);
	}

	#headlessui-portal-root .justify-center select {
		cursor: pointer;
		border-color: #5E81AC;
		background-color: #3B4252;
		color: #ECEFF4;
	}

	#headlessui-portal-root .justify-center select:focus,
	#headlessui-portal-root .justify-center select:hover {
		background-color: #434C5E;
		border-color: #88C0D0;
		box-shadow: 0 0 10px rgba(136, 192, 208, 0.3);
	}

	#headlessui-portal-root .justify-center .underline:hover,
	.markdown a {
		text-decoration: none;
	}

	/* Primary Action Buttons */
	#__next .flex.flex-row.gap-3 button.btn-primary:last-child,
	#headlessui-portal-root .bg-green-600 {
		background: linear-gradient(135deg, #88C0D0 0%, #5E81AC 100%);
		border: 1px solid #8FBCBB;
		box-shadow: 0 0 15px rgba(136, 192, 208, 0.4);
	}

	#__next .flex.flex-row.gap-3 button.btn-primary:last-child:hover,
	#headlessui-portal-root .bg-green-600:hover {
		box-shadow: 0 0 25px rgba(136, 192, 208, 0.6);
		transform: translateY(-2px);
	}

	#headlessui-portal-root .grow.justify-center {
		border-color: #5E81AC !important;
	}

	#headlessui-portal-root .grow.justify-center .border-b {
		background-color: #4C566A !important;
		border-color: #88C0D0;
	}

	#headlessui-portal-root .grow.justify-center .bg-gray-200 {
		background-color: #2E3440;
	}

	#headlessui-portal-root .grow.justify-center .bg-gray-200 .text-gray-700 {
		color: #BF616A;
	}

	#headlessui-portal-root .grow.justify-center .grid .p-4 {
		background-color: #3B4252 !important;
	}

	#__next svg[viewBox = "0 0 41 41"] {
		background: linear-gradient(135deg, #5E81AC 0%, #81A1C1 100%) !important;
		border-radius: 100%;
		border: 3px solid #88C0D0;
